// Modal.js

import React, { useState } from 'react';

const Modal = ({ isOpen, closeModal }) => {
  const [projectName, setProjectName] = useState('');
  const [projectDescription, setProjectDescription] = useState('');
  const [projectType, setProjectType] = useState('');
  const [projectDomain, setProjectDomain] = useState('');
  const [prerequisites, setPrerequisites] = useState([]);
  const [newPrerequisite, setNewPrerequisite] = useState('');

  const handleAddPrerequisite = () => {
    if (newPrerequisite.trim() !== '') {
      setPrerequisites([...prerequisites, newPrerequisite]);
      setNewPrerequisite('');
    }
  };

  const handleRemovePrerequisite = (index) => {
    const updatedPrerequisites = [...prerequisites];
    updatedPrerequisites.splice(index, 1);
    setPrerequisites(updatedPrerequisites);
  };

  return (
    <>
        <head>
             <link rel="stylesheet" href="./" ></link>
        </head>
      {isOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <h2>Add Project</h2>
            <form>
              <label>Project Name:</label>
              <input
                type="text"
                value={projectName}
                onChange={(e) => setProjectName(e.target.value)}
              />
              <label>Project Description:</label>
              <textarea
                value={projectDescription}
                onChange={(e) => setProjectDescription(e.target.value)}
              ></textarea>
              <label>Project Type:</label>
              <div>
                <input
                  type="radio"
                  value="LOP"
                  checked={projectType === 'LOP'}
                  onChange={() => setProjectType('LOP')}
                />
                <label>LOP</label>
                <input
                  type="radio"
                  value="DOP"
                  checked={projectType === 'DOP'}
                  onChange={() => setProjectType('DOP')}
                />
                <label>DOP</label>
                <input
                  type="radio"
                  value="SOP"
                  checked={projectType === 'SOP'}
                  onChange={() => setProjectType('SOP')}
                />
                <label>SOP</label>
              </div>
              <label>Project Domain:</label>
              <select
                value={projectDomain}
                onChange={(e) => setProjectDomain(e.target.value)}
              >
                <option value="CS">CS</option>
                <option value="ECE">ECE</option>
                <option value="ENI">ENI</option>
                <option value="EEE">EEE</option>
              </select>
              <label>Pre-requisites:</label>
              <div>
                <input
                  type="text"
                  value={newPrerequisite}
                  onChange={(e) => setNewPrerequisite(e.target.value)}
                />
                <button type="button" onClick={handleAddPrerequisite}>Add New</button>
              </div>
              <ul>
                {prerequisites.map((prerequisite, index) => (
                  <li key={index}>
                    {prerequisite}
                    <button type="button" onClick={() => handleRemovePrerequisite(index)}>Ã—</button>
                  </li>
                ))}
              </ul>
            </form>
          </div>
        </div>
      )}
    </>
  );
};

export default Modal;
